import React, { useState } from "react"

export default function FlipACoin(){
    const [points, setPoints] = useState(0) 
    const [myPoints, setMyPoints] = useState(10)
    function pluss(){
        if (myPoints < 0){
            console.log("You do not have enougth money");
        }else{
        setPoints(points + 1);
        setMyPoints(myPoints - 1)
        }
    }

    function less(){
        setPoints(points - 1);
        setMyPoints(myPoints + 1)
    }

    function Coin(){
        return(
            <div className="coin">
                <h1 className="coinH1">:)</h1>
            </div>
        )
    }
    
    return(
       <div  className="headOrTails">
        <button onClick={pluss}>+1</button>
        <button onClick={less}>-1</button>
        <imput type="radio" name="choose">Head</imput>
        <imput type="radio" name="choose">Tails</imput>
        <h3>My points:{myPoints}</h3>
        <h3>{points}</h3>
        < Coin/>
       </div> 
    )
}
