import React, { useState } from "react"

export default function Box(){
    const [points, setPoints] = useState(0) 
    const [myPoints, setMyPoints] = useState(10)
    function pluss(){
        setPoints(points + 1) 
    }

    function less(){
        setPoints(points - 1) 
    }

    function Coin(){
        return(
            <div classname="coin ">
                <h1>:)</h1>
            </div>
        )
    }

    return(
       <div  className="box">
        <button onClick={pluss}>+1</button>
        <button onClick={less}>-1</button>
        <h1>{points}</h1>
        < Coin/>
       </div> 
    )
}
