        if (myPoints === 0){
            console.log("You do not have enougth money");
        }else{
        setPoints(points + 1);
        setMyPoints(myPoints - 1)
        }
    }

    function less(){
        if (points < 0-4){
            console.log("Oh, you think you are smarter than me")
            setMyPoints(myPoints = 0)
        } else {
        setPoints(points - 1);
        setMyPoints(myPoints + 1)
        }
    }

