        <input type="radio" id="tail" name="side"  onChange={betting}/>
        <label htmlFor="tail">Tail</label>
        <h3> your bet: {bet ? "Head" : "Tails"}</h3>
        <h3>My points:{myPoints}</h3>
        <h3>{points}</h3>
        < Coin/>
       </div> 
    )
}
